/* com.koscom.assets.dao.AssetsMapper.getAssetsMainInfo - 자산관리(자산메인) */ SELECT COUNT( NO_ACCOUNT ) AS CNT_ACCOUNT, COUNT( NO_PERSON ) AS CNT_ITEM, SUM( AMT_BALANCE ) AS SUM_AMT_BALANCE, ( SUM(AMT_BALANCE) + SUM(AMT_EVALUATION) ) AS SUM_AMT_STOCK, SUM( AMT_EVALUATION ) AS SUM_AMT_EVALUATION FROM ASSETS_INFO WHERE (DT_EXPIRE IS NULL OR DT_EXPIRE >= TO_CHAR(SYSDATE,'YYYYMMDD')) AND YN_DELETE = 'N'
AND CD_ASSETS_CLASS = '00'
AND CD_ASSETS_CLASS = '10'
AND YN_PERSON_REGIST = 'Y' AND (CD_ASSETS_CLASS != '00' AND CD_ASSETS_CLASS != '10' AND CD_ASSETS_CLASS != '20')
AND NO_PERSON = #{no_person, jdbcType=VARCHAR}

/* com.koscom.assets.dao.AssetsMapper.listAssetsBankAccount - 자산관리(은행메인 - 계좌내역) */ SELECT NO_PERSON, NM_PERSON, RK, NO_ACCOUNT, NM_ACCOUNT, CD_FC, NM_FC, AMT_BALANCE, AMT_EVALUATION, CD_ASSETS_CLASS, CD_DETAIL_CLASS, SORT, RNUM FROM ( SELECT LST.NO_PERSON, LST.NM_PERSON, LST.RK, LST.NO_ACCOUNT, LST.NM_ACCOUNT, LST.CD_FC, LST.NM_FC, LST.AMT_BALANCE, LST.AMT_EVALUATION, LST.CD_ASSETS_CLASS, LST.CD_DETAIL_CLASS, LST.SORT, ROWNUM RNUM FROM ( SELECT TB.NO_PERSON, TB.NM_PERSON, TB.RK, TB.NO_ACCOUNT, TB.NM_ACCOUNT, TB.CD_FC, ( SELECT NM_FC FROM FC_INFO WHERE CD_FC = TB.CD_FC ) AS NM_FC, TB.AMT_BALANCE, TB.AMT_EVALUATION, TB.CD_ASSETS_CLASS, TB.CD_DETAIL_CLASS, TB.SORT FROM ( SELECT AI.NO_PERSON, ( SELECT NM_PERSON FROM PERSON_INFO WHERE NO_PERSON = AI.NO_PERSON ) AS NM_PERSON, 0 AS RK, XX1.DEC_VARCHAR2_SEL(AI.NO_ACCOUNT, '10','FS_ARIA','ASSETS_INFO','NO_ACCOUNT') AS NO_ACCOUNT, AI.NM_ACCOUNT, AI.CD_FC, AI.AMT_BALANCE, AI.AMT_EVALUATION, AI.CD_ASSETS_CLASS, AI.CD_DETAIL_CLASS, AI.SORT FROM ASSETS_INFO AI WHERE (AI.DT_EXPIRE IS NULL OR AI.DT_EXPIRE >= TO_CHAR(SYSDATE,'YYYYMMDD')) AND AI.YN_DELETE = 'N' AND AI.CD_ASSETS_CLASS = '00' AND AI.NO_PERSON = #{no_person, jdbcType=VARCHAR}
UNION ALL SELECT SI.NO_PERSON, SI.NM_PERSON, SI.RK, XX1.DEC_VARCHAR2_SEL(AI.NO_ACCOUNT, '10','FS_ARIA','ASSETS_INFO','NO_ACCOUNT') AS NO_ACCOUNT, AI.NM_ACCOUNT, AI.CD_FC, AI.AMT_BALANCE, AI.AMT_EVALUATION, AI.CD_ASSETS_CLASS, AI.CD_DETAIL_CLASS, AI.SORT FROM ( SELECT SEQ_SHARE, NO_PERSON, NM_PERSON, ROWNUM RK FROM ( SELECT SEQ_SHARE, OFFER_NO_PERSON AS NO_PERSON, OFFER_NM_PERSON AS NM_PERSON FROM SHARE_INFO WHERE SHARE_STATUS = '02' AND YN_ASSET_INFO = 'Y' AND REQ_NO_PERSON = #{no_person, jdbcType=VARCHAR} AND OFFER_NO_PERSON IN
#{share_person}
ORDER BY DT_FRT DESC ) ) SI INNER JOIN SHARE_INFO_DETAIL SID ON ( SI.SEQ_SHARE = SID.SEQ_SHARE AND SID.CD_TYPE = '00' AND SID.YN_SHARE= 'Y' ) INNER JOIN ASSETS_INFO AI ON ( SID.NO_CARD_ACC = AI.NO_ACCOUNT AND (AI.DT_EXPIRE IS NULL OR AI.DT_EXPIRE >= TO_CHAR(SYSDATE,'YYYYMMDD')) AND AI.YN_DELETE = 'N' )
) TB ORDER BY TB.RK, TB.SORT, TB.AMT_BALANCE ) LST WHERE ROWNUM <= #{endPosition, jdbcType=NUMERIC} ) WHERE RNUM >= #{startPosition, jdbcType=NUMERIC}

