UNION ALL SELECT SI.NO_PERSON, SI.NM_PERSON, SI.RK, AI.NO_ACCOUNT, AI.NM_ACCOUNT, AI.CD_FC, AI.AMT_BALANCE, AI.AMT_EVALUATION, AI.CD_ASSETS_CLASS, AI.CD_DETAIL_CLASS, AI.SORT FROM ( SELECT SEQ_SHARE, NO_PERSON, NM_PERSON, ROWNUM RK FROM ( SELECT SEQ_SHARE, OFFER_NO_PERSON AS NO_PERSON, OFFER_NM_PERSON AS NM_PERSON FROM SHARE_INFO WHERE SHARE_STATUS = '02' AND YN_ASSET_INFO = 'Y' AND REQ_NO_PERSON = #{no_person, jdbcType=VARCHAR} AND OFFER_NO_PERSON IN
