) TB

/* com.koscom.assets.dao.AssetsMapper.listAssetsSearchKeyword - 자산관리(검색키워드list) */ SELECT TB.DOC1 FROM ( SELECT DOC1, RANK() OVER( ORDER BY COUNT( DOC1 ) DESC ) AS RK FROM SCR_TRANSACTION_DETAIL WHERE 1=1
AND NO_PERSON = #{no_person, jdbcType=VARCHAR}
AND AN = XX1.ENC_VARCHAR2_INS(#{no_account, jdbcType=VARCHAR}, '11', 'FS_ARIA', 'SCR_TRANSACTION_DETAIL', 'AN')
AND AMT_DEP != '0'
AND AMT_WDRL != '0'
GROUP BY DOC1 ) TB WHERE RK <= 10

