/* com.koscom.assets.dao.AssetsMapper.listAssetsAccount - 자산관리(계좌list) */ SELECT TB.NO_PERSON, TB.NM_PERSON, TB.RK, TB.NO_ACCOUNT, TB.NM_ACCOUNT, TB.CD_FC, ( SELECT NM_FC FROM FC_INFO WHERE CD_FC = TB.CD_FC ) AS NM_FC, TB.AMT_BALANCE, TB.AMT_EVALUATION, TB.CD_ASSETS_CLASS, TB.CD_DETAIL_CLASS FROM ( SELECT AI.NO_PERSON, ( SELECT NM_PERSON FROM PERSON_INFO WHERE NO_PERSON = AI.NO_PERSON ) AS NM_PERSON, 0 AS RK, XX1.DEC_VARCHAR2_SEL(AI.NO_ACCOUNT, '10','FS_ARIA','ASSETS_INFO','NO_ACCOUNT') AS NO_ACCOUNT, AI.NM_ACCOUNT, AI.CD_FC, AI.AMT_BALANCE, AI.AMT_EVALUATION, AI.CD_ASSETS_CLASS, AI.CD_DETAIL_CLASS FROM ASSETS_INFO AI WHERE (AI.DT_EXPIRE IS NULL OR AI.DT_EXPIRE >= TO_CHAR(SYSDATE,'YYYYMMDD')) AND AI.YN_DELETE = 'N' AND AI.CD_ASSETS_CLASS = '00' AND AI.NO_PERSON = #{no_person, jdbcType=VARCHAR}
UNION ALL SELECT SI.NO_PERSON, SI.NM_PERSON, SI.RK, XX1.DEC_VARCHAR2_SEL(AI.NO_ACCOUNT, '10','FS_ARIA','ASSETS_INFO','NO_ACCOUNT') AS NO_ACCOUNT, AI.NM_ACCOUNT, AI.CD_FC, AI.AMT_BALANCE, AI.AMT_EVALUATION, AI.CD_ASSETS_CLASS, AI.CD_DETAIL_CLASS FROM ( SELECT SEQ_SHARE, NO_PERSON, NM_PERSON, ROWNUM RK FROM ( SELECT SEQ_SHARE, OFFER_NO_PERSON AS NO_PERSON, OFFER_NM_PERSON AS NM_PERSON FROM SHARE_INFO WHERE SHARE_STATUS = '02' AND YN_ASSET_INFO = 'Y' AND REQ_NO_PERSON = #{no_person, jdbcType=VARCHAR} ORDER BY DT_FRT DESC ) ) SI INNER JOIN SHARE_INFO_DETAIL SID ON ( SI.SEQ_SHARE = SID.SEQ_SHARE AND SID.CD_TYPE = '00' AND SID.YN_SHARE= 'Y' ) INNER JOIN ASSETS_INFO AI ON ( SID.NO_CARD_ACC = AI.NO_ACCOUNT AND (AI.DT_EXPIRE IS NULL OR AI.DT_EXPIRE >= TO_CHAR(SYSDATE,'YYYYMMDD')) AND AI.YN_DELETE = 'N' )
) TB ORDER BY TB.RK, TB.AMT_BALANCE

/* com.koscom.assets.dao.AssetsMapper.getDepWdrlTotalAmt - 자산관리(입출금내역 - 입금 / 출금 총액) */ SELECT SUM(STD.AMT_DEP) AS TOTAL_AMT_DEP, SUM(STD.AMT_WDRL) AS TOTAL_AMT_WDRL, COUNT(STD.AN) AS CNT_ACCOUNT FROM ( SELECT AI.NO_PERSON, ( SELECT NM_PERSON FROM PERSON_INFO WHERE NO_PERSON = AI.NO_PERSON ) AS NM_PERSON, 0 AS RK, AI.NO_ACCOUNT, AI.NM_ACCOUNT, AI.CD_FC, AI.AMT_BALANCE, AI.AMT_EVALUATION, AI.CD_ASSETS_CLASS, AI.CD_DETAIL_CLASS FROM ASSETS_INFO AI WHERE (AI.DT_EXPIRE IS NULL OR AI.DT_EXPIRE >= TO_CHAR(SYSDATE,'YYYYMMDD')) AND AI.YN_DELETE = 'N' AND AI.CD_ASSETS_CLASS = '00' AND AI.NO_PERSON = #{no_person, jdbcType=VARCHAR}
UNION ALL SELECT SI.NO_PERSON, SI.NM_PERSON, SI.RK, AI.NO_ACCOUNT, AI.NM_ACCOUNT, AI.CD_FC, AI.AMT_BALANCE, AI.AMT_EVALUATION, AI.CD_ASSETS_CLASS, AI.CD_DETAIL_CLASS FROM ( SELECT SEQ_SHARE, NO_PERSON, NM_PERSON, ROWNUM RK FROM ( SELECT SEQ_SHARE, OFFER_NO_PERSON AS NO_PERSON, OFFER_NM_PERSON AS NM_PERSON FROM SHARE_INFO WHERE SHARE_STATUS = '02' AND YN_ASSET_INFO = 'Y' AND REQ_NO_PERSON = #{no_person, jdbcType=VARCHAR} ORDER BY DT_FRT DESC ) ) SI INNER JOIN SHARE_INFO_DETAIL SID ON ( SI.SEQ_SHARE = SID.SEQ_SHARE AND SID.CD_TYPE = '00' AND SID.YN_SHARE= 'Y' ) INNER JOIN ASSETS_INFO AI ON ( SID.NO_CARD_ACC = AI.NO_ACCOUNT AND (AI.DT_EXPIRE IS NULL OR AI.DT_EXPIRE >= TO_CHAR(SYSDATE,'YYYYMMDD')) AND AI.YN_DELETE = 'N' )
) TB INNER JOIN SCR_TRANSACTION_DETAIL STD ON ( STD.NO_PERSON = TB.NO_PERSON AND STD.AN = TB.NO_ACCOUNT )

/* com.koscom.assets.dao.AssetsMapper.listAssetsDepWdrl - 자산관리(입출금내역 - list) */ SELECT NO_PERSON, NM_PERSON, RK, NO_ACCOUNT, NM_ACCOUNT, CD_FC, NM_FC, CD_ASSETS_CLASS, CD_DETAIL_CLASS, SEQ_TRAN, AN, DT_TRD, TM_TRD, AMT_DEP, AMT_WDRL, DOC1, DOC2, RNUM FROM ( SELECT LST.NO_PERSON, LST.NM_PERSON, LST.RK, LST.NO_ACCOUNT, LST.NM_ACCOUNT, LST.CD_FC, LST.NM_FC, LST.CD_ASSETS_CLASS, LST.CD_DETAIL_CLASS, LST.SEQ_TRAN, LST.AN, LST.DT_TRD, LST.TM_TRD, LST.AMT_DEP, LST.AMT_WDRL, LST.DOC1, LST.DOC2, ROWNUM RNUM FROM ( SELECT SHAI.NO_PERSON, SHAI.NM_PERSON, SHAI.RK, SHAI.NO_ACCOUNT, SHAI.NM_ACCOUNT, SHAI.CD_FC, ( SELECT NM_FC FROM FC_INFO WHERE CD_FC = SHAI.CD_FC ) AS NM_FC, SHAI.CD_ASSETS_CLASS, SHAI.CD_DETAIL_CLASS, STD.SEQ_TRAN, XX1.DEC_VARCHAR2_SEL(STD.AN, '10','FS_ARIA','SCR_TRANSACTION_DETAIL','AN') AS AN, STD.DT_TRD, STD.TM_TRD, STD.AMT_DEP, STD.AMT_WDRL, STD.DOC1, STD.DOC2 FROM ( SELECT AI.NO_PERSON, ( SELECT NM_PERSON FROM PERSON_INFO WHERE NO_PERSON = AI.NO_PERSON ) AS NM_PERSON, 0 AS RK, AI.NO_ACCOUNT, AI.NM_ACCOUNT, AI.CD_FC, AI.AMT_BALANCE, AI.AMT_EVALUATION, AI.CD_ASSETS_CLASS, AI.CD_DETAIL_CLASS FROM ASSETS_INFO AI WHERE (AI.DT_EXPIRE IS NULL OR AI.DT_EXPIRE >= TO_CHAR(SYSDATE,'YYYYMMDD')) AND AI.YN_DELETE = 'N' AND AI.CD_ASSETS_CLASS = '00' AND AI.NO_PERSON = #{no_person, jdbcType=VARCHAR}
UNION ALL SELECT SI.NO_PERSON, SI.NM_PERSON, SI.RK, AI.NO_ACCOUNT, AI.NM_ACCOUNT, AI.CD_FC, AI.AMT_BALANCE, AI.AMT_EVALUATION, AI.CD_ASSETS_CLASS, AI.CD_DETAIL_CLASS FROM ( SELECT SEQ_SHARE, NO_PERSON, NM_PERSON, ROWNUM RK FROM ( SELECT SEQ_SHARE, OFFER_NO_PERSON AS NO_PERSON, OFFER_NM_PERSON AS NM_PERSON FROM SHARE_INFO WHERE SHARE_STATUS = '02' AND YN_ASSET_INFO = 'Y' AND REQ_NO_PERSON = #{no_person, jdbcType=VARCHAR} ORDER BY DT_FRT DESC ) ) SI INNER JOIN SHARE_INFO_DETAIL SID ON ( SI.SEQ_SHARE = SID.SEQ_SHARE AND SID.CD_TYPE = '00' AND SID.YN_SHARE= 'Y' ) INNER JOIN ASSETS_INFO AI ON ( SID.NO_CARD_ACC = AI.NO_ACCOUNT AND (AI.DT_EXPIRE IS NULL OR AI.DT_EXPIRE >= TO_CHAR(SYSDATE,'YYYYMMDD')) AND AI.YN_DELETE = 'N' )
) SHAI INNER JOIN SCR_TRANSACTION_DETAIL STD ON ( STD.NO_PERSON = SHAI.NO_PERSON AND STD.AN = SHAI.NO_ACCOUNT )
ORDER BY ( STD.DT_TRD || STD.TM_TRD ) DESC ) LST WHERE ROWNUM <= #{endPosition, jdbcType=NUMERIC} ) WHERE RNUM >= #{startPosition, jdbcType=NUMERIC}

