/* com.koscom.assets.dao.AssetsMapper.getAssetsStockSumInfo - 자산관리(증권 - 전체잔고정보) */ SELECT NVL( TO_CHAR(( SUM( TOTALACCVAL )+ SUM( CASHBALANCE ))), '-' ) AS AMT_STOCK, NVL( TO_CHAR( SUM( CASHAVWITHDRAW )), '-' ) AS CASHAVWITHDRAW, NVL( TO_CHAR( SUM( SUBSMARGIN )), '-' ) AS SUBSMARGIN, NVL( TO_CHAR( SUM( RECEIVABLE )), '-' ) AS RECEIVABLE, NVL( TO_CHAR( SUM( LOANCREDIT )), '-' ) AS LOANCREDIT, NVL( TO_CHAR( SUM( VALUEATCUR )), '-' ) AS VALUEATCUR, NVL( SUM(PROLOSS), 0) AS PROLOSS FROM STOCK_SUMMARY WHERE (NO_PERSON = #{no_person, jdbcType=VARCHAR}
OR NO_PERSON IN
#{share_person}
)

/* com.koscom.assets.dao.AssetsMapper.listAssetsStockDetailCompany - 자산관리(증권 - 증권사list(검색용)) */ SELECT DISTINCT FI.NM_FC, SL.CD_FC FROM STOCK_LIST SL INNER JOIN FC_INFO FI ON SL.CD_FC = FI.CD_FC WHERE SL.YN_DELETE = 'N' AND (SL.NO_PERSON = #{no_person, jdbcType=VARCHAR}
OR SL.NO_PERSON IN
#{share_person}
)

/* com.koscom.assets.dao.AssetsMapper.listAssetsStockDetailAccount - 자산관리(증권 - 상세 계좌list) */ SELECT NO_PERSON, CD_FC, ACC_TYPE, ACC_CODE, ACC_NM, VALATCUR, PROLOSS, EARNINGRATE, TOT_SUM, PERCENT, RNUM FROM ( SELECT LST.NO_PERSON, LST.CD_FC, LST.ACC_TYPE, LST.ACC_CODE, LST.ACC_NM, LST.VALATCUR, LST.PROLOSS, LST.EARNINGRATE, LST.TOT_SUM, LST.PERCENT, ROWNUM RNUM FROM ( SELECT TB.NO_PERSON, TB.CD_FC, TB.ACC_TYPE, TB.ACC_CODE, TB.ACC_NM, TB.VALATCUR, TB.PROLOSS, TB.EARNINGRATE, SUM( TB.VALATCUR ) OVER( PARTITION BY TB.NO_PERSON ) AS TOT_SUM, ROUND(( TB.VALATCUR / SUM( TB.VALATCUR ) OVER( PARTITION BY TB.NO_PERSON ))* 100, 1 ) AS PERCENT FROM ( SELECT T.NO_PERSON, T.CD_FC, T.ACC_TYPE, T.ACC_CODE, T.ACC_NM, SUM( T.VALATTRADE ) AS VALATTRADE, SUM( T.VALATCUR ) AS VALATCUR, SUM( T.PROLOSS ) AS PROLOSS, CASE WHEN SUM( T.VALATTRADE ) > 0 THEN ROUND( SUM( T.PROLOSS )/ SUM( T.VALATTRADE )* 100, 2 ) ELSE 0 END AS EARNINGRATE FROM ( SELECT NO_PERSON, CD_FC, 'cash' AS ACC_TYPE, '' AS ACC_CODE, '현금' AS ACC_NM, '0' AS VALATTRADE, CASHBALANCE AS VALATCUR, '0' AS PROLOSS, 0 AS EARNINGRATE FROM STOCK_SUMMARY WHERE 1=1
AND ACCNO = #{no_account, jdbcType=VARCHAR}
UNION ALL SELECT NO_PERSON, CD_FC, 'shr' AS ACC_TYPE, ISINCODE AS ACC_CODE, SUBSTR(ISINCODE, 4, 6) AS ACC_NM, VALATTRADE AS VALATTRADE, VALATCUR AS VALATCUR, PROLOSS, EARNINGRATE FROM STOCK_EQUITYLIST WHERE 1=1
AND ACCNO = #{no_account, jdbcType=VARCHAR}
UNION ALL SELECT NO_PERSON, CD_FC, 'fnd' AS ACC_TYPE, FUNDCODE AS ACC_CODE, FUNDNAME AS ACC_NM, VALATTRADE AS VALATTRADE, VALATCUR AS VALATCUR, PROLOSS, EARNINGRATE FROM STOCK_FUNDLIST WHERE 1=1
AND ACCNO = #{no_account, jdbcType=VARCHAR}
ORDER BY ACC_NM ) T GROUP BY T.NO_PERSON, T.CD_FC, T.ACC_TYPE, T.ACC_CODE, T.ACC_NM ) TB WHERE 1=1 AND (TB.NO_PERSON = #{no_person, jdbcType=VARCHAR}
OR TB.NO_PERSON IN
#{share_person}
)
AND TB.ACC_TYPE = #{scAccType, jdbcType=VARCHAR}
AND TB.CD_FC = #{scCompany, jdbcType=VARCHAR}
ORDER BY
TB.VALATCUR DESC, /*평가금액순*/
TB.EARNINGRATE DESC, /*상승률순*/
ROUND((TB.VALATCUR / SUM(TB.VALATCUR) OVER (PARTITION BY TB.NO_PERSON)) * 100, 3) DESC, /*비중순*/
TB.CD_FC, TB.ACC_TYPE, TB.ACC_NM ) LST WHERE ROWNUM <= #{endPosition, jdbcType=NUMERIC} ) WHERE RNUM >= #{startPosition, jdbcType=NUMERIC}

/* com.koscom.assets.dao.AssetsMapper.listAssetsStockDetailAccountCount - 자산관리(증권 - 계좌listCount) */ SELECT COUNT(*) AS recordCount FROM ( SELECT T.NO_PERSON, T.CD_FC, T.ACC_TYPE, T.ACC_CODE, T.ACC_NM, SUM( T.VALATTRADE ) AS VALATTRADE, SUM( T.VALATCUR ) AS VALATCUR, SUM( T.PROLOSS ) AS PROLOSS, CASE WHEN SUM( T.VALATTRADE ) > 0 THEN ROUND( SUM( T.PROLOSS )/ SUM( T.VALATTRADE )* 100, 2 ) ELSE 0 END AS EARNINGRATE FROM ( SELECT NO_PERSON, CD_FC, 'cash' AS ACC_TYPE, '' AS ACC_CODE, '현금' AS ACC_NM, '0' AS VALATTRADE, CASHBALANCE AS VALATCUR, '0' AS PROLOSS, 0 AS EARNINGRATE FROM STOCK_SUMMARY WHERE 1=1
AND ACCNO = #{no_account, jdbcType=VARCHAR}
UNION ALL SELECT NO_PERSON, CD_FC, 'shr' AS ACC_TYPE, ISINCODE AS ACC_CODE, SUBSTR(ISINCODE,4,6) AS ACC_NM, VALATTRADE AS VALATTRADE, VALATCUR AS VALATCUR, PROLOSS, EARNINGRATE FROM STOCK_EQUITYLIST WHERE 1=1
AND ACCNO = #{no_account, jdbcType=VARCHAR}
UNION ALL SELECT NO_PERSON, CD_FC, 'fnd' AS ACC_TYPE, FUNDCODE AS ACC_CODE, FUNDNAME AS ACC_NM, VALATTRADE AS VALATTRADE, VALATCUR AS VALATCUR, PROLOSS, EARNINGRATE FROM STOCK_FUNDLIST WHERE 1=1
AND ACCNO = #{no_account, jdbcType=VARCHAR}
ORDER BY ACC_NM ) T GROUP BY T.NO_PERSON, T.CD_FC, T.ACC_TYPE, T.ACC_CODE, T.ACC_NM ) TB WHERE 1=1 AND (TB.NO_PERSON = #{no_person, jdbcType=VARCHAR}
OR TB.NO_PERSON IN
#{share_person}
)
AND TB.ACC_TYPE = #{scAccType, jdbcType=VARCHAR}
AND TB.CD_FC = #{scCompany, jdbcType=VARCHAR}

    	/* com.koscom.assets.dao.AssetsMapper.getAssetsStockFndInfo - 자산관리(증권 - 펀드정보) */
        SELECT
			FUNDNAME,
			VALATCUR,
			PROLOSS,
			EARNINGRATE,
			VALATTRADE
		FROM
			STOCK_FUNDLIST
		WHERE NO_PERSON = #{no_person, jdbcType=VARCHAR}
			AND ACCNO = #{no_account, jdbcType=VARCHAR}
			AND FUNDCODE = #{acc_code, jdbcType=VARCHAR}
