sql.where:
			
			
				
					
						BO.yn_popup = 'Y'
					
					
						AND title    LIKE '%'||#{txt_detail}||'%'
						AND content  LIKE '%'||#{txt_detail}||'%'
						AND seq      LIKE '%'||#{txt_detail}||'%'
						AND yn_popup LIKE '%'||#{txt_detail}||'%'
					
				
			

			
				
					
						
							AND TO_CHAR(dt_frt,'yyyyMMdd') >= #{txt_dt_from}
							AND TO_CHAR(dt_lst,'yyyyMMdd') >= #{txt_dt_from}
						
						
							AND TO_CHAR(dt_frt,'yyyyMMdd') <= #{txt_dt_to}
							AND TO_CHAR(dt_lst,'yyyyMMdd') <= #{txt_dt_to}
						
					
					
						
							AND dt_frt >= #{txt_dt_from}
							AND dt_lst >= #{txt_dt_from}
						
						
							AND dt_frt <= #{txt_dt_to}
							AND dt_lst <= #{txt_dt_to}
						
					
				
			

			
				AND BO.id_board = #{id_board}
			
				AND BO.yn_use = 'Y'

mapper.select:
SELECT * FROM (SELECT LST.seq , LST.seq_group , LST.seq_level , LST.seq_order , LST.id_board , LST.title , LST.hit , LST.pass_board , LST.content , LST.url_files1 , LST.url_files2 , LST.yn_use , LST.yn_popup , LST.id_frt , LST.dt_frt , LST.id_lst , LST.dt_lst , LST.nm_person , ROWNUM RNUM , ROWNUM || LST.seq || LST.id_board AS BOARD_IDX , LST.cd_event_proc , LST.ymd_post_strt , LST.ymd_post_end FROM (SELECT BO.seq , BO.seq_group , BO.seq_level , BO.seq_order , BO.id_board , BO.title , BO.hit , BO.pass_board , BO.content , BO.url_files1 , BO.url_files2 , BO.yn_use , BO.yn_popup , BO.id_frt
,TO_CHAR(BO.DT_FRT,'YYYYMMDD') AS dt_frt , BO.id_lst , BO.dt_lst , PE.nm_person , BO.cd_event_proc , BO.ymd_post_strt , BO.ymd_post_end FROM board_info BO LEFT OUTER JOIN person_info PE ON BO.id_frt = PE.no_person
ORDER BY BO.seq DESC ) LST WHERE ROWNUM <= #{endPosition, jdbcType=NUMERIC} ) WHERE RNUM >= #{startPosition, jdbcType=NUMERIC}

	SELECT tb.img_files, tb.nm_img_files
	FROM (
    	SELECT
			img_files, nm_img_files
			, ROW_NUMBER() OVER(ORDER BY dt_frt DESC) AS rnum
		FROM BOARD_FILE_INFO
		WHERE ID_BOARD = #{id_board, jdbcType=VARCHAR}
			AND SEQ = #{seq}
			AND FILE_TYPE = #{file_type, jdbcType=VARCHAR}
		) tb
	WHERE tb.rnum = 1
