mapper.select:
SELECT NVL(B.CD_FC, A.CD_FC) AS CD_FC FROM FC_INFO A LEFT OUTER JOIN FC_NM_INFO B ON A.CD_FC = B.CD_FC WHERE (B.NM_FC = REGEXP_REPLACE(#{nm_fc, jdbcType=VARCHAR}, '( ){1,}', '') OR A.NM_FC = REGEXP_REPLACE(#{nm_fc, jdbcType=VARCHAR}, '( ){1,}', ''))
AND A.CD_FIN = #{cd_fin, jdbcType = VARCHAR}
AND B.YN_USE = 'Y' AND ROWNUM = 1

SELECT FC.CODE_GROUP AS FIELD_CD , FC.NM_CODE AS FIELD_NM , FC.FIELD_LENGTH AS FIELD_LEN , FC.ITEM_TAG AS FIELD_TAG , SD.TYPE_DATA AS FIELD_TYPE , SD.NM_COL AS FIELD_DB_NM , FC.TYPE_ATTR AS FIELD_TYPE_ATTR , (CASE WHEN (SELECT COUNT(*) FROM FC_EDOC_REPEAT FCRT WHERE FCRT.CD_FC = FC.CD_FC AND FCRT.NO_EDOC = FC.NO_EDOC AND FCRT.TYPE_TXRX = FC.TYPE_TXRX AND FCRT.PARENT_CODE_GROUP = FC.CODE_GROUP GROUP BY FCRT.PARENT_CODE_GROUP ) > 0 THEN 'Y' ELSE 'N' END ) AS REPEAT_YN FROM FC_EDOC_CD FC INNER JOIN STD_CD SD ON FC.ITEM_TAG = SD.CODE_GROUP AND SD.CODE_VALUE = ' '
AND FC.cd_fc = #{cd_fc}
AND FC.no_edoc = #{no_edoc}
AND FC.type_txrx = #{type_txrx}
AND FC.code_value = ' ' AND FC.YN_USE = 'Y' AND NOT EXISTS (SELECT FCR.REAL_CODE_GROUP FROM FC_EDOC_REPEAT FCR WHERE FC.CD_FC = FCR.CD_FC AND FC.NO_EDOC = FCR.NO_EDOC AND FC.TYPE_TXRX = FCR.TYPE_TXRX AND FC.CODE_GROUP = FCR.REAL_CODE_GROUP)
ORDER BY FC.SEQ_ORDER ASC

	SELECT CODE_VALUE
	  FROM FC_EDOC_CD
	 
		
			AND cd_fc = #{cd_fc}
		
		
			AND no_edoc = #{no_edoc}
		
		
			AND item_tag = #{item_tag}
		
		
			AND item_tag_value = #{item_tag_value}
		
    	
			AND type_txrx = #{type_txrx}
		
		AND yn_use = 'Y'

	SELECT ITEM_TAG_VALUE
	  FROM FC_EDOC_CD
	 
		
			AND cd_fc = #{cd_fc}
		
		
			AND no_edoc = #{no_edoc}
		
		
			AND item_tag = #{item_tag}
		
		
			AND code_value = #{code_value}
		
        
			AND type_txrx = #{type_txrx}
		
		AND yn_use = 'Y'
