mapper.select:
/* com.koscom.news.dao.NewsMapper.listApiNews */ SELECT SEQ_NEWS, SEQ_SEARCH, TITLE, LINK, DESCRIPTION, PUB_DATE, NEWS_COMPANY, LOCAL_LINK_TEXT, LOCAL_LINK, CONTENTS, NEWS_STATUS, KEYWORD, YN_USE, DT_WRT, DT_FRT, SEARCH_QUERY, SEQ_THUM_FILE, RNUM FROM ( SELECT LST.SEQ_NEWS, LST.SEQ_SEARCH, LST.TITLE, LST.LINK, LST.DESCRIPTION, LST.PUB_DATE, LST.NEWS_COMPANY, LST.LOCAL_LINK_TEXT, LST.LOCAL_LINK, LST.CONTENTS, LST.NEWS_STATUS, LST.KEYWORD, LST.YN_USE, LST.DT_WRT, LST.DT_FRT, LST.SEARCH_QUERY, LST.SEQ_THUM_FILE, ROWNUM RNUM FROM ( SELECT ANR.SEQ_NEWS, ANR.SEQ_SEARCH, ANR.TITLE, ANR.LINK, ANR.DESCRIPTION, DECODE( SUBSTR( TO_CHAR( ANR.PUB_DATE, 'MM' ), 1, 1 ), '0', SUBSTR( TO_CHAR( ANR.PUB_DATE, 'MM' ), 2, 1 ), TO_CHAR( ANR.PUB_DATE, 'MM' ))|| '/' || DECODE( SUBSTR( TO_CHAR( ANR.PUB_DATE, 'DD' ), 1, 1 ), '0', SUBSTR( TO_CHAR( ANR.PUB_DATE, 'DD' ), 2, 1 ), TO_CHAR( ANR.PUB_DATE, 'DD' )) AS PUB_DATE, ANR.NEWS_COMPANY, ANR.LOCAL_LINK_TEXT, ANR.LOCAL_LINK, ANR.CONTENTS, ANR.NEWS_STATUS, ANR.KEYWORD, ANR.YN_USE, ANR.DT_WRT, ANR.DT_FRT, AN.SEARCH_QUERY, (SELECT SEQ_FILE FROM API_NEWS_FILE_INFO ANFI WHERE ANFI.SEQ_NEWS = ANR.SEQ_NEWS AND FILE_TYPE='01') AS SEQ_THUM_FILE FROM API_NEWS_RESULT ANR, API_NEWS AN
ORDER BY
ANR.PUB_DATE DESC /*최신날짜순*/,
ANR.HITS DESC /*조회수순*/,
ANR.DT_FRT DESC ) LST WHERE ROWNUM <= #{endPosition, jdbcType=NUMERIC} ) WHERE RNUM >= #{startPosition, jdbcType=NUMERIC}
