	/* com.koscom.person.dao.PersonMapper.modifyCdPush */
    MERGE INTO PUSH_SETTING_INFO
    USING DUAL
    ON (no_person = #{no_person, jdbcType=VARCHAR} AND CD_PUSH = #{item_push, jdbcType=VARCHAR})
