        YN_PUSH = #{stat_push}
    WHEN NOT MATCHED THEN
    INSERT(
        NO_PERSON
        , CD_PUSH
        , YN_PUSH
        , ID_FRT
        , DT_FRT
    ) VALUES (
        #{no_person,jdbcType=VARCHAR}
        , #{item_push,jdbcType=VARCHAR}
        , #{stat_push,jdbcType=VARCHAR}
        , #{no_person,jdbcType=VARCHAR}
        , SYSDATE
    )

