mapper.select:
/* com.koscom.person.dao.PersonMapper.listPersonShareInfoSummary : 공유관리 요약list */ SELECT SI.SEQ_SHARE, SI.REQ_NO_PERSON, SI.REQ_NM_PERSON, SI.REQ_HP, SI.OFFER_NO_PERSON, SI.OFFER_NM_PERSON, SI.OFFER_HP, SI.SHARE_STATUS, SI.YN_CREDIT_OFFER, SI.YN_DEBT_OFFER, SI.YN_INCOME_OFFER, SI.YN_ACCOUNT_OFFER, SI.YN_CARD_OFFER, SI.YN_DEBT_ITGT_MNGM, SI.YN_ASSET_ITGT_MNGM, SI.YN_CONSUME_ITGT_MNGM, SI.DT_STT_OFFER, SI.DT_END_OFFER, SI.YN_OFFER, SI.YN_ITGT_MNGM, SI.ID_FRT, SI.DT_FRT, SI.ID_LST, SI.DT_LST FROM ( SELECT SEQ_SHARE, REQ_NO_PERSON, REQ_NM_PERSON, REQ_HP, OFFER_NO_PERSON, OFFER_NM_PERSON, OFFER_HP, SHARE_STATUS, YN_CREDIT_OFFER, YN_DEBT_OFFER, YN_INCOME_OFFER, YN_ACCOUNT_OFFER, YN_CARD_OFFER, YN_DEBT_ITGT_MNGM, YN_ASSET_ITGT_MNGM, YN_CONSUME_ITGT_MNGM, DT_STT_OFFER, DT_END_OFFER, ( CASE WHEN YN_CREDIT_OFFER = 'Y' OR YN_DEBT_OFFER = 'Y' OR YN_INCOME_OFFER = 'Y' OR YN_ACCOUNT_OFFER = 'Y' OR YN_CARD_OFFER = 'Y' THEN 'Y' ELSE 'N' END ) AS YN_OFFER, ( CASE WHEN YN_DEBT_ITGT_MNGM = 'Y' OR YN_ASSET_ITGT_MNGM = 'Y' OR YN_CONSUME_ITGT_MNGM = 'Y' THEN 'Y' ELSE 'N' END ) AS YN_ITGT_MNGM, ID_FRT, TO_CHAR(DT_FRT, 'YYYYMMDD') AS DT_FRT, ID_LST, TO_CHAR(DT_LST, 'YYYYMMDD') AS DT_LST FROM SHARE_INFO WHERE (SHARE_STATUS != '03' AND SHARE_STATUS != '04')
AND REQ_NO_PERSON = #{no_person, jdbcType=NUMERIC}
AND OFFER_NO_PERSON = #{no_person, jdbcType=NUMERIC}
ORDER BY DT_FRT DESC ) SI WHERE ROWNUM <= 3

