		COUNT(*) AS CHK_CNT
	FROM
		SHARE_MESSAGE_INFO
	WHERE
		DT_REQ = TO_CHAR( SYSDATE, 'YYYYMMDD' )
		AND SEQ_SHARE = #{seq_share, jdbcType=NUMERIC}
		AND CD_MESSAGE = #{cd_message, jdbcType=VARCHAR}

